import React from 'react';
import Transition from 'react-transition-group/Transition';
import classes from './Backdrop.module.css';

export default function Backdrop(props) {

    // let backdrop = <div
    //                     className={classes.Backdrop}>
    //                     {props.children}
    //                 </div>;

    // if(!props.show) {
    //     backdrop = null;
    // }

    return (
        <Transition
        in={props.show}
        timeout={800}
        mountOnEnter
        unmountOnExit>
            {state => {
                const cssClasses = [
                    classes.Backdrop,
                    state === 'entering' 
                        ? classes.show
                        : state === 'exiting'
                        ? classes.Hide : null
                ];
                return (
                    <div
                        className={cssClasses.join(' ')}>
                        {props.children}
                    </div>
                );
            }
            }
        </Transition>
    );
}
