import React from 'react';
import Transition from 'react-transition-group/Transition';

import classes from './OpenCard.module.css';

// import Backdrop from '../Backdrop/Backdrop';
import Button from '../UI/Button/Button';
import OpenCardQuote from '../OpenCardQuote/OpenCardQuote';

// import meme from '../../assets/meme.jpg';

export default function OpenCard(props) {
    console.log('rendering opencard, props: ', props);

    return (
        // <Backdrop
        //     show={props.card}>
            <Transition
                in={props.show}
                timeout={400}
                mountOnEnter
                unmountOnExit>
                {state => {
                    const cssClasses = [
                        classes.OpenCard,
                        state === 'entering'
                        ? classes.Open
                        : state === 'exiting' 
                        ? classes.Closed
                        : null
                    ];

                    const quoteClasses = [
                        classes.Quote__lineLeft,
                        state === 'entered'
                        ? classes.Shown
                        : null,
                        state === 'exiting'
                        ? classes.StaticVisible
                        : null
                    ];

                    const quoteClassesTwo = [
                        classes.Quote__lineRight,
                        state === 'entered'
                        ? classes.ShownTwo
                        : null,
                        state === 'exiting'
                        ? classes.StaticVisible
                        : null
                    ];

                    const artistClasses = [
                        classes.Quote__artist,
                        state === 'entered'
                        ? classes.ShownThree
                        : null,
                        state === 'exiting'
                        ? classes.StaticVisible
                        : null
                    ];

                    const funFactClasses = [
                        classes.FunFact,
                        state === 'entered'
                        ? classes.Visible
                        : null
                    ];

                    const buttonClasses = [
                        classes.ButtonContainer,
                        state === 'entered'
                        ? classes.Visible
                        : null,
                        state === 'entered'
                        ? classes.Wiggle
                        : null
                    ];

                    const quoteDivClasses = [
                        classes.Quote,
                        state === 'entered'
                        ? classes.BorderVisible
                        : null
                    ]

                    const renderedCard = (
                            <div
                                className={cssClasses.join(' ')}>
                                <div style={{width: '80%', margin: '0 auto'}}>
                                    <div className={quoteDivClasses.join(' ')}>
                                        <h3>Du hast ein Match : )</h3>
                                            <p className={quoteClasses.join(' ')}>"{props.card.fullQuote[0]}</p>
                                            <p className={quoteClassesTwo.join(' ')}>{props.card.fullQuote[1]}"</p>
                                            <p className={artistClasses.join(' ')}>{props.card.artist}</p>
                                        </div>
                                        {/* <p>Fast eine Freude wie auf Tinder, oder?</p> nur beim ersten Match */}
                                    <div className={funFactClasses.join(' ')}>
                                        <h3>Wusstest du schon, dass...</h3>
                                        <p>Queen teilweise mit zehn Songs gleichzeitig in der Top 10 waren?</p>
                                    </div>
                                </div>
                                <div 
                                    className={buttonClasses.join(' ')}>
                                    <Button 
                                        clickHandler={props.onClose}
                                        type='button'
                                        look='literature'>
                                        Weiter
                                    </Button>
                                </div>
                            </div>
                    );
                

                    return renderedCard;
                }}

            </Transition>
        // </Backdrop>
    );
}
