import React, { useState } from 'react';
import classes from './About.module.css';

import { info } from '../../data/about';

import Button from '../../components/UI/Button/Button';
import AboutCard from '../../components/AboutCard/AboutCard';
import DetailCard from '../../components/DetailCard/DetailCard';

export default function About(props) {
    console.log(info);
    const [ detailCard, setDetailCard ] = useState();

    function setDetailCardHandler(id) {
        console.log('setDetailCard, id: ', id);
        const selectedCard = info.find(i => i.id === id);
        setDetailCard(selectedCard);
    }

    function closeDetailCard() {
        setDetailCard(null);
    }

    return (
        <div>
            <div className={classes.Intro}>
                <h2>Thank you</h2>
                <p>Thank you very much for checking out my app and for your interest to learn more about it. I appreciate that!</p>
            </div>
            <div className={classes.Container}>
                {info.map(i => {
                    return <AboutCard
                        key={i.id}
                        id={i.id}
                        onMoreDetails={setDetailCardHandler}>{i.title}</AboutCard>;
                })}
            </div>
            <div className={classes.Buttons}>
                <Button
                    look="music"
                    type="button"
                    clickHandler={props.onCloseAboutAndReset}>Return home</Button>
                <Button
                    look="literature"
                    type="button"
                    clickHandler={props.onCloseAbout}>Continue your game</Button>
            </div>
            {detailCard && <DetailCard />}
        </div>
    )
};
