import React from 'react';
import Transition from 'react-transition-group/Transition';

import classes from './OpenCard.module.css';

// import Backdrop from '../Backdrop/Backdrop';
import Button from '../UI/Button/Button';
import OpenCardQuote from '../OpenCardQuote/OpenCardQuote';

// import meme from '../../assets/meme.jpg';

export default function OpenCard(props) {
    console.log('rendering opencard');

    return (
        // <Backdrop
        //     show={props.card}>
            <Transition
                in={props.show}
                timeout={800}
                mountOnEnter
                unmountOnExit>
                {state => {
                    const cssClasses = [
                        classes.OpenCard,
                        state === 'entering'
                        ? classes.Open
                        : state === 'exiting' 
                        ? classes.Closed
                        : null
                    ];

                    const quoteClasses = [
                        classes.quote__lineLeft,
                        state === 'entered'
                        ? classes.Shown
                        : null
                    ];

                let renderedCard = (
                        <div
                            className={cssClasses.join(' ')}>
                            {props.card && <p>{props.card.title}</p>}
                            <div 
                                className={classes.ButtonContainer}>
                                <Button 
                                    clickHandler={props.onClose}
                                    type='button'
                                    look='literature'>
                                        Weiter
                                    </Button>
                            </div>
                        </div>
                );
                
                if(props.cardsMatch) {
                    renderedCard = (
                            <div
                                className={cssClasses.join(' ')}>
                                <div style={{width: '80%', margin: '0 auto'}}>
                                        {/* Den Part zu Song infos in extra Component, auch wg. Animation */}
                                        {/* <div className={classes.Quote}>
                                            <p className={classes.Quote__lineLeft}>"{props.card.fullQuote[0]}</p>
                                            <p className={classes.Quote__lineRight}>{props.card.fullQuote[1]}"</p>
                                            <p className={classes.Quote__artist}>{props.card.artist}</p>
                                        </div> */}
                                        {/* <OpenCardQuote 
                                            quote={props.card.fullQuote}
                                            artist={props.card.artist}/> */}
                                <div className={classes.Quote}>
                                        <p>{state}</p>
                                            <p className={quoteClasses.join(' ')}>"{props.card.fullQuote[0]}</p>
                                            <p className={classes.Quote__lineRight}>{props.card.fullQuote[1]}"</p>
                                            <p className={classes.Quote__artist}>{props.card.artist}</p>
                                        </div>
                                        <h3>Du hast ein Match : )</h3>
                                        <p>Fast eine Freude wie auf Tinder, oder?</p>
                                        <h3>Wusstest du schon, dass...</h3>
                                        <p>Queen teilweise mit zehn Songs gleichzeitig in der Top 10 waren?</p>
                                        {/* <p>Diese Zeile stammt aus dem Song "{props.card.song}" von {props.card.artist}.</p>
                                        Erschienen ist der Song {props.card.year} auf dem Album {props.card.record}. */}
                                </div>
                                <div 
                                    className={classes.ButtonContainer}>
                                    <Button 
                                        clickHandler={props.onClose}
                                        type='button'
                                        look='literature'>
                                        Weiter
                                    </Button>
                                </div>
                            </div>
                    );
                }

                    return renderedCard;
                }}

            </Transition>
        // </Backdrop>
    );
}
