import React from 'react';
import Transition from 'react-transition-group/Transition';

import classes from './OpenCard.module.css';

import Backdrop from '../Backdrop/Backdrop';

// import meme from '../../assets/meme.jpg';

export default function OpenCard(props) {

    console.log(props);

    return (
        // <Backdrop
        //     show={props.card}>
            <Transition
                in={props.show}
                timeout={800}
                mountOnEnter
                unmountOnExit>
                {state => {
                    const cssClasses = [
                        classes.OpenCard,
                        state === 'entering'
                        ? classes.Open
                        : state === 'exiting' 
                        ? classes.Closed
                        : null
                    ];

                let renderedCard = (
                        <div
                            className={cssClasses.join(' ')}>
                            {props.card && <p>{props.card.title}</p>}
                            <div 
                                className={classes.ButtonContainer}
                                onClick={props.onClose}>
                                x
                            </div>
                        </div>
                );
                
                if(props.cardsMatch) {
                    renderedCard = (
                            <div
                                className={cssClasses.join(' ')}>
                                <h2>You found a match</h2>
                                <div
                                    className={classes.CardWrapper}>
                                    <div
                                        className={classes.CardContent}>
                                        {props.card && <p>{props.card.title}</p>}
                                    </div>
                                    <div
                                        className={classes.CardContent}>
                                        {props.card && <p>{props.card.title}</p>}
                                    </div>
                                </div>
                                <div 
                                    className={classes.ButtonContainer}
                                    onClick={props.onClose}>
                                    Weiterspielen
                                </div>
                            </div>
                    );
                }

                    return renderedCard;
                }}

            </Transition>
        // </Backdrop>
    );
}
