import { useState } from 'react';

import { memcards } from '../../data/Cards';

import MemoryCard from '../MemoryCard/MemoryCard';
import Modal from '../Modal/Modal';

import classes from './MemoryCards.module.css';

function shuffle(array) {
    let n = array.length -1;
    for(let i = n; i > 0; i--){
        const j = Math.floor(Math.random() * i)
        const temp = array[i]
        array[i] = array[j]
        array[j] = temp;
      }
    return array;
}

const MemoryCards = () => {

    const [cards, setCards] = useState(shuffle(memcards));
    const [counter, setCounter] = useState(0);
    const [revealedCards, setRevealedCards] = useState(0);
    const [isOver, setIsOver] = useState(false);
    const [isMatch, setIsMatch] = useState(false);

    function switchOneCard() {
        const newArrangedCards = cards;
        let randomIndexOne = Math.floor((Math.random() * 11));
        let randomIndexTwo = Math.floor((Math.random() * 11));
        if(randomIndexOne === randomIndexTwo
            || newArrangedCards[randomIndexOne].title === newArrangedCards[randomIndexTwo].title) {
                randomIndexTwo = Math.floor(Math.random() * 11);
            }
        const changedCard = newArrangedCards.splice(randomIndexOne, 1);
        newArrangedCards.splice(randomIndexTwo, 0, changedCard[0]);
        setCards(newArrangedCards);
    
    }

    const cardCover = () => {
        const updatedCards = cards
            .map(card => {
                return {
                    ...card,
                    revealed: card.pairFound
                }
            });
        setCards(updatedCards);
        setRevealedCards(0);
    }

    const cardClickHandler = (cardId) => {
        if(revealedCards >= 2) {
            return;
        }
        setRevealedCards(prevRevealedCards => prevRevealedCards + 1);
        const selectedCardIndex = cards.findIndex(card => card.id === cardId);
        const selectedCard = cards[selectedCardIndex];
        selectedCard.revealed = true;
        const updatedCards = [...cards];
        updatedCards[selectedCardIndex] = selectedCard;
        setCards(updatedCards);
        
        setCounter(prevCounter => prevCounter + 1);
        console.log(revealedCards);

        // checking both revealed cards for pairing

        if(revealedCards === 1) {
            const visibleCards = updatedCards.filter(card => card.revealed && !card.pairFound);
            console.log('visible cards:', visibleCards);
            if(visibleCards[0].pairId === visibleCards[1].pairId) {
                for(const card of visibleCards) {
                    card.pairFound = true;
                }
                setRevealedCards(0);
                if(updatedCards.filter(card => !card.pairFound).length === 0) {
                    setIsOver(true);
                    return alert('You made it. You won. Game is over.');
                }
                return setIsMatch(true);
            }
        }

        if(revealedCards === 1) {
            // checking if all pairs are found
            console.log('setting timeout');
            setTimeout(() => {
                cardCover();
                }, 1000);
            }
        }

    function reset() {
        const updatedCards = [...cards];
        updatedCards.forEach(card => card.pairFound = false);
    }

    const renderedCards = cards.map(card => {
        return <MemoryCard 
            card={card} 
            key={card.id}
            clickHandler={cardClickHandler}
            enabled={!revealedCards >= 2}
            />;
    });

    return (
        <div>
            <button
                onClick={switchOneCard}>Re-arrange</button>
            <div className={classes.MemoryCards}>
                {isMatch && <Modal closeModal={() => setIsMatch(false)}>Yeah! You got a match!</Modal>}
                {isOver && <Modal closeModal={() => {
                    setIsMatch(false);
                    setIsOver(false);
                }}>You won! Great! I hope you enjoyed it!</Modal>}
                {!isOver && renderedCards}
                {isOver && <h2>You made it!</h2>}
            </div>
        </div>

    );
}

export default MemoryCards;
