import React, { useState, useEffect } from 'react';

import MemoryCards from './components/MemoryCards/MemoryCards';
import Start from './pages/Start/Start';
import Final from './pages/Final/Final';
import Header from './components/Layout/Header/Header';
import About from './pages/About/About';

import { MemoryContext } from './context/memoryContext';

import { memcards } from './data/Cards';

import './App.css';

function App() {

  const [started, setStarted] = useState();
  // isOver default ändern
  const [isOver, setIsOver] = useState();
  // set counter to zero
  const [counter, setCounter] = useState(3);
  const [mode, setMode] = useState('memes');
  const [showAbout, setShowAbout] = useState();
  const [memoryCards, setMemoryCards] = useState();
  const [revealCards, setRevealCards] = useState();

  // const contextValue = {
  //   cards: memoryCards,
  //   startGame: startGameHandler(),
  //   resetGame: reset()
  // };

  console.log('RERENDER APP, cards in APP: ', memoryCards);

  function shuffle(array) {
    let n = array.length -1;
    for(let i = n; i > 0; i--){
        const j = Math.floor(Math.random() * i)
        const temp = array[i]
        array[i] = array[j]
        array[j] = temp;
      }
    return array;
}

  function reset() {
    console.log('RESET');
    setStarted(false);
    setIsOver(false);
    setCounter(0);
    setMode();
    setMemoryCards(null);
  }

  function startGameHandler() {
    const cards = memcards.map(card => {
      return {
        ...card,
        revealed: false
      }
    })
    setMemoryCards(shuffle(cards));
    setIsOver(false);
    setStarted(true);
  }

  function finishGame() {
    setIsOver(true);
  }

  function increaseCounter() {
    console.log('increasee');
    setCounter(prevCounter => prevCounter + 1);
  }

  function selectModeHandler(mode) {
    setMode(mode);
    console.log(mode);
  }

  function showAboutHandler() {
    console.log('onShowAbout')
    setShowAbout(true);
  }

  function closeAboutHandler() {
    setShowAbout(false);
    setRevealCards(true);
  }

  function closeAboutAndResetHandler() {
    console.log('closeAndCancel');
    setShowAbout(false);
    reset();
  }

  return (
    <React.Fragment>
      {/* <MemoryContext.Provider> */}
      <Header 
        count={counter}
        activeMode={mode}
        onSelectMode={selectModeHandler}
        onShowAbout={showAboutHandler}/>
        <div className='App'>
          {!started && !showAbout && <Start onStart={startGameHandler}/>}
          {started && !isOver && !showAbout && <MemoryCards 
                          cards={memoryCards}
                          increaseCount={increaseCounter}
                          onFinish={finishGame}
                          isOver={isOver}
                          count={counter}
                          revealCards={revealCards}/>}
          <Final 
                        show={isOver && !showAbout}
                        onStart={startGameHandler}
                        counter={counter}/>
      </div>
      {showAbout && <About 
        onCloseAbout={closeAboutHandler}
        onCloseAboutAndReset={closeAboutAndResetHandler}/>}
      {/* </MemoryContext.Provider> */}
    </React.Fragment>
  );
}

export default App;
