import React from 'react';
import Transition from 'react-transition-group/Transition';
import classes from './Backdrop.module.css';

export default function Backdrop(props) {

    // let backdrop = <div
    //                     className={classes.Backdrop}>
    //                     {props.children}
    //                 </div>;

    // if(!props.show) {
    //     backdrop = null;
    // }

    return (
        <Transition
        in={props.show}
        timeout={800}
        mountOnEnter
        unmountOnExit>
            <div
                className={classes.Backdrop}>
                {props.children}
            </div>;
        </Transition>
    );
}
