import React from 'react';
import classes from './DetailCard.module.css';

import Button from '../../components/UI/Button/Button';

export default function DetailCard(props) {
    console.log('props.card: ', props);

    let techStack;
    let links;

    if(props.card.techStack) {
        techStack = (
        <React.Fragment>
        <div
            className={classes.Legend}>
            <span>Basics</span>
            <span>Good</span>
            <span>Very good</span>
        </div>
        <ul
            className={classes.List}>
            { props.card.techStack.map(item => {
            return (
                <li
                    className={classes.ListItem}
                    style={{width: item.level}}>
                    {item.title}
                    {/* <span>{item.level}</span> */}
                </li>
                );
            })}
        </ul>
        </React.Fragment>    )};

    if(props.card.links) {
        links = (
            <ul>
            {props.card.links.map(link => {
                return (
                    <a 
                        href={link.link}
                        target="_blank"
                        rel='noreferrer'>
                        <li>
                            {link.title}
                        </li>
                     </a>
                );
            })}
        </ul>
        );
    }

    return (
        <div
            className={classes.DetailCard}>
                {props.card.image && <div className={classes.ImageContainer}><img src={props.card.image} alt={props.card.alt} /></div>}
            <h2>{props.card.title}</h2>
            {/* <p>{props.card.content}</p> */}
            {techStack && techStack}
            {links && links}
            <Button
                type="button"
                look="music"
                clickHandler={props.closeCard}>Close</Button>
        </div>
    );
}
