import React, { useState } from 'react';

import MemoryCards from './components/MemoryCards/MemoryCards';
import Start from './pages/Start/Start';
import Final from './pages/Final/Final';
import Header from './components/Layout/Header/Header';

import './App.css';

function App() {

  const [started, setStarted] = useState(false);
  const [isOver, setIsOver] = useState(false);
  const [counter, setCounter] = useState(0);
  const [mode, setMode] = useState('memes');

  function startGameHandler() {
    setStarted(true);
  }

  function finishGame() {
    setIsOver(true);
  }

  function increaseCounter() {
    console.log('increasee');
    setCounter(prevCounter => prevCounter + 1);
  }

  function selectModeHandler(mode) {
    setMode(mode);
    console.log(mode);
  }

  console.log('counter', counter);

  return (
    <React.Fragment>
      <Header 
        count={counter}
        activeMode={mode}
        onSelectMode={selectModeHandler}/>
        <div className='App'>
          {!started && <Start onStart={startGameHandler}/>}
          {started && <MemoryCards 
                          increaseCount={increaseCounter}
                          finish={finishGame}
                          isOver={isOver}
                          count={counter}/>}
          {isOver && <Final />}
      </div>
    </React.Fragment>
  );
}

export default App;
