import React, { useState, useEffect, useCallback } from 'react';

// import { memcards } from '../../data/Cards'

import MemoryCard from '../MemoryCard/MemoryCard';
import Backdrop from '../Backdrop/Backdrop';
import OpenCard from '../OpenCard/OpenCard';

import classes from './MemoryCards.module.css';

function shuffle(array) {
    let n = array.length -1;
    for(let i = n; i > 0; i--){
        const j = Math.floor(Math.random() * i)
        const temp = array[i]
        array[i] = array[j]
        array[j] = temp;
      }
    return array;
}

const MemoryCards = (props) => {
    
    // const copiedCards = props.cards.slice();
    // const shuffledCards = shuffle(copiedCards);

    const [cards, setCards] = useState(props.cards);
    const [revealedCards, setRevealedCards] = useState(0);
    // const [isOver, setIsOver] = useState(false);
    const [isMatch, setIsMatch] = useState(false);
    const [focusedCard, setFocusedCard] = useState();
    // const [cardIsClicked, setCardIsClicked] = useState();
    const [showBackdrop, setShowBackdrop] = useState();

    const { isOver, finish } = props;


    useEffect(() => {
    }, [cards]);

    const cardCover = useCallback(() => {
        setRevealedCards(0);
        const updatedCards = cards
        .map(card => {
            return {
                ...card,
                revealed: card.pairFound
            }
        });
        setCards(updatedCards);
    }, [cards, setCards, setRevealedCards]);

    useEffect(() => {
        'use effect to clean up / unreveal cards';
  
        return cardCover();
    }, []); 

    const blurCard = useCallback(() => {
            setIsMatch(false);
            // setCardIsClicked(false);
            setTimeout(() => {
                setShowBackdrop(false);
                setFocusedCard(null);
            }, 500);
            if(revealedCards > 1) {
                // checking if all pairs are found
                setTimeout(() => {
                    cardCover();
                    }, 1200);
                }
                if(cards.filter(card => !card.pairFound).length === 0) {
                    return props.onFinish();
                 }
        }, [revealedCards, cards, isOver, finish, setIsMatch, setShowBackdrop, setFocusedCard, cardCover]); 

    useEffect(() => {
        if(!props.isOver && revealedCards === 2) {
            blurCard();
        }
    }, [revealedCards, props.isOver, blurCard]);

    const cardClickHandler = (cardId) => {
        if(revealedCards >= 2) {
            return;
        }
        setRevealedCards(prevRevealedCards => prevRevealedCards + 1);
        props.increaseCount();
        // setCardIsClicked(true);
        
        setFocusedCard(cards.find(card => card.id === cardId));
        const updatedCards = [...cards];
        const selectedCardIndex = updatedCards.findIndex(card => card.id === cardId);
        const selectedCard = updatedCards[selectedCardIndex];
        selectedCard.revealed = true;     
        updatedCards[selectedCardIndex] = selectedCard;
        
        setCards(updatedCards);

        // checking both revealed cards for pairing
        
        if(revealedCards === 1) {
            const visibleCards = updatedCards.filter(card => card.revealed && !card.pairFound);
            if(visibleCards[0].pairId === visibleCards[1].pairId) {
                for(const card of visibleCards) {
                    card.pairFound = true;
                }
                setRevealedCards(0);
                setShowBackdrop(true);
                return setIsMatch(true);
                }
            }
        }

    const renderedCards = cards.map(card => {
        return <MemoryCard 
            card={card} 
            key={card.id}
            clickHandler={cardClickHandler}
            disabled={revealedCards >= 2}
            cards={cards}
            />;
    });

    if(!props.show) {
        return null;
    }

    return (
            <div className={classes.MemoryCards}>
                {!props.isOver && renderedCards}
                    <Backdrop show={showBackdrop} />
                    <OpenCard 
                        show={isMatch}
                        card={focusedCard}
                        cardsMatch={isMatch}
                        onClose={blurCard}/>
            </div>
    );
}

export default MemoryCards;
