import React from 'react';
import Transition from 'react-transition-group/Transition';

import classes from './OpenCard.module.css';

// import Backdrop from '../Backdrop/Backdrop';
import Button from '../UI/Button/Button';

// import meme from '../../assets/meme.jpg';

export default function OpenCard(props) {

    console.log(props);

    return (
        // <Backdrop
        //     show={props.card}>
            <Transition
                in={props.show}
                timeout={800}
                mountOnEnter
                unmountOnExit>
                {state => {
                    const cssClasses = [
                        classes.OpenCard,
                        state === 'entering'
                        ? classes.Open
                        : state === 'exiting' 
                        ? classes.Closed
                        : null
                    ];

                let renderedCard = (
                        <div
                            className={cssClasses.join(' ')}>
                            {props.card && <p>{props.card.title}</p>}
                            <div 
                                className={classes.ButtonContainer}>
                                <Button 
                                    clickHandler={props.onClose}
                                    type='button'
                                    look='literature'>
                                        Weiter
                                    </Button>
                            </div>
                        </div>
                );
                
                if(props.cardsMatch) {
                    renderedCard = (
                            <div
                                className={cssClasses.join(' ')}>
                                    {/* <div
                                        className={classes.CardContent}>
                                        {props.card && <p>{props.card.title}</p>}
                                    </div>
                                    <div
                                        className={classes.CardContent}>
                                        {props.card && <p>{props.card.title}</p>}
                                    </div> */}
                                    <div style={{width: '80%', margin: '0 auto'}}>
                                        {/* Den Part zu Song infos in extra Component, auch wg. Animation */}
                                        <div className={classes.Quote}>
                                            <p>"{props.card.fullQuote[0]}</p>
                                            <p style={{textAlign: 'right'}}>{props.card.fullQuote[1]}"</p>
                                            <p>{props.card.artist}</p>
                                        </div>
                                        <h3>Du hast ein Match!</h3>
                                        <p>Fast eine Freude wie auf Tinder, oder?</p>
                                        <p>Diese Zeile stammt aus dem Song "{props.card.song}" von {props.card.artist}.</p>
                                        Erschienen ist der Song {props.card.year} auf dem Album {props.card.record}.
                                    </div>
                                <div 
                                    className={classes.ButtonContainer}>
                                    <Button 
                                        clickHandler={props.onClose}
                                        type='button'
                                        look='literature'>
                                        Weiter
                                    </Button>
                                </div>
                            </div>
                    );
                }

                    return renderedCard;
                }}

            </Transition>
        // </Backdrop>
    );
}
