import React from 'react';
import Transition from 'react-transition-group/Transition';

import classes from './OpenCard.module.css';

import Button from '../UI/Button/Button';

export default function OpenCard(props) {

    return (
            <Transition
                in={props.show}
                timeout={400}
                mountOnEnter
                unmountOnExit>
                {state => {
                    const cssClasses = [
                        classes.OpenCard,
                        state === 'entering'
                        ? classes.Open
                        : state === 'exiting' 
                        ? classes.Closed
                        : null
                    ];

                    const quoteClasses = [
                        classes.Quote__lineLeft,
                        state === 'entered'
                        ? classes.Shown
                        : null,
                        state === 'exiting'
                        ? classes.StaticVisible
                        : null
                    ];

                    const quoteClassesTwo = [
                        classes.Quote__lineRight,
                        state === 'entered'
                        ? classes.ShownTwo
                        : null,
                        state === 'exiting'
                        ? classes.StaticVisible
                        : null
                    ];

                    const artistClasses = [
                        classes.Quote__artist,
                        state === 'entered'
                        ? classes.ShownThree
                        : null,
                        state === 'exiting'
                        ? classes.StaticVisible
                        : null
                    ];

                    const funFactClasses = [
                        classes.FunFact,
                        state === 'entered'
                        ? classes.Visible
                        : null
                    ];

                    const buttonClasses = [
                        classes.ButtonContainer,
                        state === 'entered'
                        ? classes.Visible
                        : null,
                        state === 'entered'
                        ? classes.Wiggle
                        : null
                    ];

                    const quoteDivClasses = [
                        classes.Quote,
                        state === 'entered'
                        ? classes.BorderVisible
                        : null
                    ]

                    const renderedCard = (
                            <div
                                className={cssClasses.join(' ')}>
                                    <div className={quoteDivClasses.join(' ')}>
                                            <p className={quoteClasses.join(' ')}><span>" </span>{props.card.fullQuote[0]}</p>
                                            <p className={quoteClassesTwo.join(' ')}>{props.card.fullQuote[1]}<span>"</span></p>
                                            <p className={artistClasses.join(' ')}>{props.card.artist}</p>
                                        </div>
                                    <div className={funFactClasses.join(' ')}>
                                        <h3>Did you know?</h3>
                                        {props.card.fact && <p>{props.card.fact}</p>}
                                    </div>
                                <div 
                                    className={buttonClasses.join(' ')}>
                                    <Button 
                                        clickHandler={props.onClose}
                                        type='button'
                                        look='default'>
                                        GO ON
                                    </Button>
                                </div>
                            </div>
                    );
                

                    return renderedCard;
                }}
            </Transition>
    );
}
