import { useState } from 'react';

import MemoryCard from '../MemoryCard/MemoryCard';

import classes from './MemoryCards.module.css';

const memoryCards = [
    {
        id: 1,
        pairId: 'A',
        title: 'A',
        revealed: false,
    },
    {
        id: 2,
        pairId: 'A',
        title: 'A',
        revealed: false,
    },
    {
        id: 3,
        pairId: 'B',
        title: 'B',
        revealed: false,
    },
    {
        id: 4,
        pairId: 'B',
        title: 'B',
        revealed: false,
    },
    {
        id: 5,
        pairId: 'C',
        title: 'C',
        revealed: false,
    },
    {
        id: 6,
        pairId: 'D',
        title: 'D',
        revealed: false,
    }
];

const MemoryCards = () => {

    const [cards, setCards] = useState(memoryCards);
    const [counter, setCounter] = useState(0);
    const [cardIsRevealed, setCardIsRevealed] = useState(false);

    console.log(counter);

    const cardCover = () => {
        const updatedCards = cards.map(card => {
            return {
                ...card,
                revealed: false
            }
        });
        setCards(updatedCards);
        setCardIsRevealed(false);
    }

    const cardClickHandler = (cardId) => {
        if(cardIsRevealed) {
            return;
        }
        setCardIsRevealed(true);
        const selectedCardIndex = cards.findIndex(card => card.id === cardId);
        const selectedCard = cards[selectedCardIndex];
        selectedCard.revealed = true;
        const updatedCards = [...cards];
        updatedCards[selectedCardIndex] = selectedCard;
        setCards(updatedCards);
        setCounter(prevCounter => prevCounter + 1);
        setTimeout(() => {
            cardCover();
            }, 1000);
        }

    const renderedCards = cards.map(card => {
        return <MemoryCard 
            card={card} 
            key={card.id}
            clickHandler={cardClickHandler}
            enabled={!cardIsRevealed}
            />;
    });



    return (
        <div className={classes.MemoryCards}>
            {renderedCards}
        </div>
    );
}

export default MemoryCards;
