import React from 'react';
import classes from './DetailCard.module.css';

import Button from '../../components/UI/Button/Button';

export default function DetailCard(props) {
    console.log('props.card: ', props);

    let techStack;

    if(props.card.techStack) {
        techStack = <ul>
            { props.techStack.map(item => {
            return (
                <li>
                    <span>{item.title}</span>
                    <span>{item.level}</span>
                </li>
            );
        })}
        </ul>
        

    }

    return (
        <div
            className={classes.DetailCard}>
                {props.card.image && <div className={classes.ImageContainer}><img src={props.card.image} alt={props.card.alt} /></div>}
            <h2>{props.card.title}</h2>
            <p>{props.card.content}</p>
            {techStack && techStack}
            <Button
                type="button"
                look="music"
                clickHandler={props.closeCard}>Close</Button>
        </div>
    );
}
