import React, { useState, useCallback, useEffect } from 'react';
import Transition from 'react-transition-group/Transition';
import classes from './Final.module.css';

import Button from '../../components/UI/Button/Button';

export default function Final(props) {

    // const [shownCounter, setShownCounter] = useState(0);
    console.log(props.show);

    // const incrementCounterInterval = setInterval(() => {
    //     setShownCounter(prevCounter => prevCounter + 1);
    // }, 1000);

    // const startCounting = () => incrementCounterInterval();

    // useEffect(() => {
    //     console.log('setting interval');
    //     startCounting();
    // }, []);
    

    // incrementCounterInterval();

    // if(shownCounter >= props.counter) {
    //     console.log('showncounter > 5');
    //     clearInterval(incrementCounterInterval);
    // }

    function copy() {
        const copyText = document.getElementById('link');
        copyText.select();
        document.execCommand("copy"); 
    }

    return (
        <div className={classes.Final}>
            <Transition
                in={props.show}
                timeout={100}
                mountOnEnter
                unmountOnExit>
                {state => {
                    console.log('state: ', state);
                    const summaryClasses = [
                        classes.Summary,
                        state === 'entered'
                        ? classes.Visible
                        : null
                    ];

                    return (
                    <div>
                    {/* einfaden lassen nacheinander */}
                    <h2>Congratulations</h2>
                    <div
                        className={summaryClasses.join(' ')}>
                        <p>You found all pairs with just {props.counter} tries.</p>
                        <p>Thank you for using my app!</p>
                        <h3>Sharing is caring</h3>
                        <p>If you enjoyed this game, please share it with your friends. Click on the button below to copy the link. I appreciate it!</p>
                        <input type="text" value="localhost" id="link"/>
                        <Button>Save link to clipboard</Button>
                    </div>
                    
                <h3>Play memory using another topic</h3>
                {/* rein sliden links / rechts */}
                <div
                    className={classes.ButtonOne}>
                    <Button
                        clickHandler={props.onStart}
                        ype='button'
                        look='music'>
                        Music
                    </Button>
                </div>
                <div
                    className={classes.ButtonTwo}>
                    <Button
                        clickHandler={props.onStart}
                        type='button'
                        look='literature'>
                        Literature
                </Button>
                </div>
                <div
                    className={classes.ButtonThree}>
                    <Button
                        clickHandler={props.onStart}
                        type='button'
                        look='politics'>
                        Politics
                    </Button>
                    </div>
                </div>
                );
            }}

            </Transition>
        </div>
    )
};
