import classes from './Button.module.css';

function Button(props) {

    let color;

    switch(props.look) {
        case ('music'):
            color = '#0057E7';
            break;
        case ('politics'):
            color = '#D62D20';
            break;
        case ('literature'):
            color = '#FFA700';
            break;
        case ('default'):
            color = '#0057E7';
            break;
        default: 
            color = 'pink';
            break;
    }

    return (
        <button
            className={classes.Button}
            style={{backgroundColor: color}}
            onClick={props.clickHandler}
            type={props.type}>
            {props.children}
        </button>
    );
}

export default Button;
