import React, { useState } from 'react';

import MemoryCards from './components/MemoryCards/MemoryCards';
import Start from './pages/Start/Start';
import Final from './pages/Final/Final';
import Header from './components/Layout/Header/Header';
import About from './pages/About/About';

import { rockCards, metalCards } from './data/Cards';

import './App.css';

function App() {

  const [started, setStarted] = useState();
  // isOver default ändern
  const [isOver, setIsOver] = useState();
  // set counter to zero
  const [counter, setCounter] = useState(3);
  const [mode, setMode] = useState();
  const [showAbout, setShowAbout] = useState();
  const [memoryCards, setMemoryCards] = useState();

  function shuffle(array) {
    let n = array.length -1;
    for(let i = n; i > 0; i--){
        const j = Math.floor(Math.random() * i)
        const temp = array[i]
        array[i] = array[j]
        array[j] = temp;
      }
    return array;
}

  function reset() {
    setStarted(false);
    setIsOver(false);
    setCounter(0);
    setMode();
    setMemoryCards(null);
  }

  function startGameHandler(style) {
    let cards;
    if(style === 'ROCK') {
      cards = rockCards.map(card => {
        return {
          ...card,
          revealed: false
        }
      });
    }
    if(style === 'METAL') {
      cards = metalCards.map(card => {
        return {
          ...card,
          revealed: false
        }
      });
    }
    setMemoryCards(shuffle(cards));
    setIsOver(false);
    setStarted(true);
  }

  function finishGame() {
    setIsOver(true);
  }

  function increaseCounter() {
    setCounter(prevCounter => prevCounter + 1);
  }

  function selectModeHandler(mode) {
    setMode(mode);
    console.log(mode);
  }

  function showAboutHandler() {
    setShowAbout(true);
  }

  function closeAboutHandler() {
    setShowAbout(false);
  }

  function closeAboutAndResetHandler() {
    setShowAbout(false);
    reset();
  }

  return (
    <React.Fragment>
      <Header 
        count={counter}
        activeMode={mode}
        onSelectMode={selectModeHandler}
        onShowAbout={showAboutHandler}/>
        <div className='App'>
          {!started && !showAbout && <Start onStart={startGameHandler}/>}
          {started && !isOver && <MemoryCards 
                          show={!showAbout}
                          cards={memoryCards}
                          increaseCount={increaseCounter}
                          onFinish={finishGame}
                          isOver={isOver}
                          count={counter}/>}
          <Final 
                        show={isOver && !showAbout}
                        onStart={startGameHandler}
                        counter={counter}/>
      </div>
      {showAbout && <About 
        gameIsRunning={started}
        onCloseAbout={closeAboutHandler}
        onCloseAboutAndReset={closeAboutAndResetHandler}/>}
    </React.Fragment>
  );
}

export default App;
